        cases :0,
        deaths:0,
        recoveries:0

    })
    useEffect(()=>{
        const fetchAffect =async ()=>{
           const affect =  await getAffected()
           console.log(affect)
           setData(affect)
           console.log(data)
        }
        fetchAffect()
    },[])
    return <div>
    <select>
        <option value="US">US</option>
        <option value="In"></option>
    </select>
      <p>{data.cases}</p>
      <p>{data.deaths}</p>
      <p>{data.recoveries}</p>
    </div>
}
